import React, { useState } from "react";

const Table = () => {
  const [expandedRows, setExpandedRows] = useState([]);

  const toggleRow = (rowId) => {
    if (expandedRows.includes(rowId)) {
      setExpandedRows(expandedRows.filter((id) => id !== rowId));
    } else {
      setExpandedRows([...expandedRows, rowId]);
    }
  };

  const isRowExpanded = (rowId) => expandedRows.includes(rowId);

  const data = [
    {
      id: 1,
      company: "Metafy",
      title: "Design Lead",
      location: "Remote",
      content: "Collapsible content for Row 1",
    },
    {
      id: 2,
      company: "Another Company",
      title: "Another Title",
      location: "Office",
      content: "Collapsible content for Row 2",
    },
    // Add more data as needed
  ];

  return (
    <div style={{ height: "727px", width: "100%", border: "2px solid black" }}>
      <div className="container mb-5">
        <table
          className="table caption-top"
          style={{
            border: "1px solid black",
            fontSize: "38px",
          }}
        >
          <tbody>
            {data.map((row) => (
              <React.Fragment key={row.id}>
                <tr style={{ padding: "22.8px 15px" }}>
                  <th style={{ padding: "22.8px 15px" }} scope="row">
                    Current
                  </th>
                  <td style={{ padding: "22.8px 15px" }}>{row.company}</td>
                  <td style={{ padding: "22.8px 15px" }}>{row.title}</td>
                  <td style={{ padding: "22.8px 15px" }}>{row.location}</td>
                  <th
                    style={{ padding: "22.8px 15px" }}
                    onClick={() => toggleRow(row.id)}
                    className={`clickable ${
                      isRowExpanded(row.id) ? "active" : ""
                    }`}
                  >
                    {isRowExpanded(row.id) ? "-" : "+"}
                  </th>
                </tr>
                {isRowExpanded(row.id) && (
                  <tr className="container" style={{ padding: "22.8px 15px" }}>
                    <td colSpan="4">{row.content}</td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Table;
