import React, { useState } from "react";

const Experience1 = () => {
  const [expandedRows, setExpandedRows] = useState([]);

  const toggleRow = (rowId) => {
    if (expandedRows.includes(rowId)) {
      setExpandedRows(expandedRows.filter((id) => id !== rowId));
    } else {
      setExpandedRows([...expandedRows, rowId]);
    }
  };

  const isRowExpanded = (rowId) => expandedRows.includes(rowId);

  const data = [
    {
      id: 1,
      attendance: "Current",
      company: "Metafy",
      title: "Design Lead",
      location: "Remote",
      content: "Collapsible content for Row 1",
    },
    {
      id: 2,
      attendance: "2020-2022",
      company: "MetaLab",
      title: "Design Lead",
      location: "Remote",
      content: "Collapsible content for Row 2",
    },
    {
      id: 3,
      attendance: "2018-2020",
      company: "Huawei",
      title: "UX Design Manager",
      location: "Milan, Italy",
      content: "Collapsible content for Row 2",
    },
    {
      id: 4,
      attendance: "2018",
      company: "Alkemy",
      title: "Senior Product Designer",
      location: "Milan, Italy",
      content: "Collapsible content for Row 2",
    },
    {
      id: 5,
      attendance: "2020-2022",
      company: "JibJab",
      title: "Senior Product Designer",
      location: "Los Angles, California",
      content: "Collapsible content for Row 2",
    },
    {
      id: 6,
      attendance: "2018-2020",
      company: "Huawei",
      title: "Senior Product Designer",
      location: "Milan, Italy",
      content: "Collapsible content for Row 2",
    },
    {
      id: 7,
      attendance: "2007-2013",
      company: "Pbcom",
      title: "Digital Art Director",
      location: "Genoa, Tlaly",
      content: "Collapsible content for Row 2",
    },
    // Add more data as needed
  ];

  return (
    <div
      style={{
        height: "auto",
        width: "100%",
        border: "2px solid black",
      }}
    >
      <div className="container">
        <table
          className="table caption-top"
          style={{
            border: "1px solid black",
            fontSize: "35px",
            marginBottom: "0px",
          }}
        >
          <tbody>
            {data.map((row) => (
              <React.Fragment key={row.id}>
                <tr style={{ padding: "22.8px 15px" }}>
                  <th style={{ padding: "22.8px 15px" }} scope="row">
                    {row.attendance}
                  </th>
                  <td style={{ padding: "22.8px 15px" }}>{row.company}</td>
                  <td style={{ padding: "22.8px 15px" }}>{row.title}</td>
                  <td style={{ padding: "22.8px 15px" }}>{row.location}</td>
                  <th
                    style={{
                      padding: "22.8px 15px",
                      fontWeight: "900",
                      fontSize: "40px",
                    }}
                    onClick={() => toggleRow(row.id)}
                    className={`clickable ${
                      isRowExpanded(row.id) ? "active" : ""
                    }`}
                  >
                    {isRowExpanded(row.id) ? "-" : "+"}
                  </th>
                </tr>
                {isRowExpanded(row.id) && (
                  <tr className="container" style={{ padding: "22.8px 15px" }}>
                    <td colSpan="5">{row.content}</td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Experience1;
