import React from "react";

const Table = () => {
  return (
    <table class="table">
      <thead>
        <tr>
          <th>Header 1</th>
          <th>Header 2</th>
        </tr>
      </thead>
      <tbody>
        {persons.map((item) => {
          return (
            <tr style={{ padding: "22.8px 15px" }}>
              <th style={{ padding: "22.8px 15px" }} scope="row">
                {item.textOne}
              </th>
              <td style={{ padding: "22.8px 15px" }}>{item.textTwo}</td>
              <td style={{ padding: "22.8px 15px" }}>{item.textThree}</td>
              <td style={{ padding: "22.8px 15px" }}>{item.textFour}</td>
              <th
                style={{ padding: "22.8px 15px" }}
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                {item.textFive}
                <div>
                  <ul
                    class="dropdown-menu"
                    style={{
                      border: "2px solid black",
                      padding: "0px",
                      borderRadius: "none",
                      width: "85.1%",
                    }}
                  >
                    <td
                      class="dropdown-item"
                      style={{
                        fontSize: "20px",
                      }}
                    >
                      Action
                    </td>
                    <td
                      class="dropdown-item"
                      style={{
                        fontSize: "20px",
                      }}
                    >
                      Another action
                    </td>
                    <td
                      class="dropdown-item"
                      style={{
                        fontSize: "20px",
                      }}
                    >
                      Something else here
                    </td>
                    <td
                      class="dropdown-item"
                      style={{
                        fontSize: "20px",
                      }}
                    >
                      Separated link
                    </td>
                  </ul>
                </div>
              </th>
            </tr>
          );
        })}
        <tr data-bs-toggle="collapse" data-bs-target="#row1" class="clickable">
          <td>Row 1 Data 1</td>
          <td>Row 1 Data 2</td>
        </tr>
        <tr id="row1" class="collapse">
          <td colspan="2">Collapsible content for Row 1</td>
        </tr>
        <tr data-bs-toggle="collapse" data-bs-target="#row2" class="clickable">
          <td>Row 2 Data 1</td>
          <td>Row 2 Data 2</td>
        </tr>
        <tr id="row2" class="collapse">
          <td colspan="2">Collapsible content for Row 2</td>
        </tr>
      </tbody>
    </table>
  );
};

export default Table;
